SELECT name FROM people JOIN stars ON stars.person_id = people.id WHERE name != 'Kevin Bacon' AND  movie_id IN (SELECT movie_id FROM stars WHERE person_id IS (SELECT id FROM people WHERE name = 'Kevin Bacon' AND birth = 1958));
